
public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		for(int i=0;i<10;++i) {
			int b = a*i;
			System.out.println( a +" * "+ i +" = " + b);
			// break ou continue permet d'arreter une boucle ou de continuer une boucle
		}
	}

}
